import React from 'react';
import styled from 'styled-components';

function AdminAuth () {
    return(
        <Container>
            <AdminForm>
                <FormTitle>Admin Panel</FormTitle>
                <PasswordInput type="password" />
                <SubmitForm>Ingresar</SubmitForm>
            </AdminForm>
        </Container>
    );
}

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const AdminForm = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    border: rgb(231, 166, 51) solid 1px;
    padding: 50px;
    border-radius: 3px;
`;

const FormTitle = styled.p`
    font-size: 34px;
    color: #ffffff;
`;

const PasswordInput = styled.input`
    font-size: 20px;
    padding: 5px 0;
    background: none;
    border: none;
    border-bottom: #E7A633 solid 2px;
    color: #ffffff;
    margin: 10px 0;
`;

const SubmitForm = styled.button`
    outline: none;
    background: rgb(231, 166, 51);
    color: #ffffff;
    font-size: 20px;
    padding: 10px 15px;
    border: none;
    border-radius: 3px;
    width: 100%;
    cursor: pointer;
    transform: skew(-.100rad) !important;
    font-weight: bold;
`;


export default AdminAuth;
